mapper.select:
SELECT EVAL_ID , T2.LINE_CODE_NM_KR AS EVAL_EMP_GRP_CD , T3.LINE_CODE_NM_KR AS EVAL_TYPE_CD , EVAL_TYPE_WGT , EVAL_PLUS_POINT , T1.USE_YN FROM HRE_EVAL_EMP_TYPE T1 JOIN CMN_CODE_LINE T2 ON T1.EVAL_EMP_GRP_CD = T2.LINE_CODE JOIN CMN_CODE_LINE T3 ON T1.EVAL_TYPE_CD = T3.LINE_CODE ORDER BY T2.LINE_CODE_NM_KR, T1.EVAL_TYPE_CD
AND EVAL_TYPE_CD = #{evalTypeCd}
AND EVAL_EMP_GRP_CD = #{evalEmpGrpCd}
