mapper.select:
SELECT EVAL_ID , EVAL_GRADE , EVAL_POINT_RANGE , EVAL_POINT_ROUND_OPTION , USE_YN FROM HRE_EVAL_GRADE
AND EVAL_ID LIKE '%' || #{evalId} || '%'
AND EVAL_GRADE = #{evalGrade}

mapper.insert:
insert into 
	hre_eval_grade( EVAL_ID
                    , EVAL_GRADE
                    , EVAL_POINT_RANGE
                    , USE_YN ) 
                    values(
                    #{evalId}
                    ,#{evalGrade}
                    , #{evalPointRange}
                    , #{useYn})

mapper.update:
UPDATE HRE_EVAL_GRADE
   SET EVAL_POINT_RANGE	= #{evalPointRange}
     , USE_YN = #{useYn}
 WHERE EVAL_ID = #{evalId}

mapper.delete:
DELETE
  FROM HRE_EVAL_GRADE
 WHERE EVAL_ID = #{evalId}
 AND	EVAL_GRADE = #{evalGrade}

mapper.select:
SELECT COUNT(*)
  FROM HRE_EVAL_GRADE
 WHERE EVAL_ID = #{evalId}
   AND EVAL_GRADE = #{evalGrade}
