mapper.select:
/*Hre3030-mapper.getEvalTypeList*/ SELECT EVAL_YEAR , EVAL_ID , EVAL_STEP_CD , EVAL_STEP_START_DATE , EVAL_STEP_END_DATE , USE_YN FROM HRE_EVAL_STEP
AND EVAL_YEAR = #{evalYear}
ORDER BY EVAL_ID

mapper.insert:
        /* Hre3030-mapper.insertEvalStep */
        INSERT
          INTO HRE_EVAL_STEP (  EVAL_YEAR
				       		  , EVAL_ID
				       		  , EVAL_STEP_CD
				       		  , EVAL_STEP_START_DATE
				       		  , EVAL_STEP_END_DATE
				       		  , USE_YN)
                   VALUES ( #{evalYear}
                          , #{evalId}
                          , #{evalStepCd}
                          , #{evalStepStartDate}
                          , #{evalStepEndDate}
                          , #{useYn})

mapper.update:
/* Hre3030-mapper.updateEvalStep */
UPDATE HRE_EVAL_STEP
   SET EVAL_YEAR    	     = #{evalYear}
     , EVAL_STEP_START_DATE  = #{evalStepStartDate}
     , EVAL_STEP_END_DATE    = #{evalStepEndDate}
     , USE_YN           	 = #{useYn}
 WHERE EVAL_ID   			 = #{evalId}
   AND EVAL_STEP_CD          = #{evalStepCd}

mapper.delete:
/* Hre3030-mapper.deleteEvalStep */
DELETE
  FROM HRE_EVAL_STEP
 WHERE EVAL_ID         = #{evalId}
   AND EVAL_STEP_CD    = #{evalStepCd}
