mapper.select:
/*Hre3040-mapper.getEvalTypeList*/ SELECT T.EVAL_ID , T.EVAL_TYPE_CD , T.EVAL_METHOD_CD , T.EVAL_ADJUST_METHOD , T.USE_YN FROM HRE_EVAL_TYPE T
AND T.EVAL_ID = #{evalId}
ORDER BY T.EVAL_ID

mapper.insert:
        /* Hre3030-mapper.insertEvalType */
        INSERT
          INTO HRE_EVAL_TYPE (  EVAL_ID
				       		  , EVAL_TYPE_CD
				       		  , EVAL_METHOD_CD
				       		  , EVAL_ADJUST_METHOD
				       		  , USE_YN)
                   VALUES ( #{evalId}
                          , #{evalTypeCd}
                          , #{evalMethodCd}
                          , #{evalAdjustMethod}
                          , #{useYn})

mapper.update:
        /* Hre3030-mapper.updateEvalType */
        UPDATE HRE_EVAL_TYPE
           SET  EVAL_METHOD_CD        = #{evalMethodCd}    
             ,  EVAL_ADJUST_METHOD    = #{evalAdjustMethod}
	         ,  USE_YN                = #{useYn}     
         WHERE  EVAL_ID               = #{evalId}
           AND 	EVAL_TYPE_CD          = #{evalTypeCd}

mapper.delete:
/* Hre3030-mapper.deleteEvalType */
DELETE
  FROM HRE_EVAL_TYPE
 WHERE EVAL_ID         = #{evalId}
   AND EVAL_TYPE_CD    = #{evalTypeCd}
